                                list = General.GetQueryable<Booking>(c => (c.PickupDateTime.Value.Date==DateTime.Now.Date && c.PickupDateTime.Value.TimeOfDay >= fromDate.Value.TimeOfDay && c.PickupDateTime.Value.TimeOfDay <= tillDate.Value.TimeOfDay)
