        private int GetTargetRowIndex(GridDataRowElement row, Point dropLocation)
        {
            // row.BackColor = Color.LightBlue;
            int halfHeight = row.Size.Height / 2;
            int index = row.RowInfo.Index;
            if (dropLocation.Y > halfHeight)
            {
                index++;
            }
            return index;
        }

